function validateActivityForm() {
    var name = document.forms["create-activity"]["name"].value;
    var start = document.forms["create-activity"]["start"].value;
    var end = document.forms["create-activity"]["end"].value;
    var date = document.forms["create-activity"]["date"].value;
    var desc = document.forms["create-activity"]["desc"].value;
    var status = document.forms["create-activity"]["status"].value;
    var type = document.forms["create-activity"]["type"].value;
    var location = document.forms["create-activity"]["location"].value;
    var file = document.forms["create-activity"]["fileToUpload"].value;
    if (name == "") {
        alert("Activity name field must be filled out");
        return false;
    }
    if (name.length > 15) {
        alert("Activity name max 15 character");
        return false;
    }
    if (start == "") {
        alert("Registration start date field must be filled out");
        return false;
    }
    if (end == "") {
        alert("Registration end date field must be filled out");
        return false;
    }
    if (date == "") {
        alert("Activity date field must be filled out");
        return false;
    }
    if (desc == "") {
        alert("Activity description field must be filled out");
        return false;
    }
    if (status == "null") {
        alert("Activity status field must be filled out");
        return false;
    }
    if (type == "null") {
        alert("Activity type field must be filled out");
        return false;
    }
    if (location == "") {
        alert("Activity location field must be filled out");
        return false;
    }
    if (file == "") {
        alert("Activity pamphlet needed");
        return false;
    }
}

$("a").hover(function(e) {
    $($(this).data("tooltip")).css({
        left: e.pageX +1,
        top: e.pageY +1,
    }).stop().show(100);
}, function() {
    $($(this).data("tooltip")).hide();
});